targets.expr:
(
  rate(process_cpu_time_seconds_total{
    process_owner=~".*gl-user-server",
    state="user",
    host_id="${instance_id}"
  }[1m]) 
  * on (process_pid) group_left(session_id)
  (
    server_up{
      host_id="${instance_id}", 
      session_id=~".+"
    }
  )
) * 100 / on() group_left() count(
  system_cpu_utilization_ratio{state="idle", host_id="${instance_id}"}
)
